import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { loginUser } from '../'
import { StyleFrom } from '../style/StyleForm'
import { useNavigate } from 'react-router'

function Login() {
    const dispatch = useDispatch()
    const auth = useSelector((state)=>state.auth)
    const navigate = useNavigate()

    console.log(auth)




    useEffect(()=>{
      if(auth._id){
        navigate("/cart")
      }
    },[auth._id,navigate])

    const [user,setUser] = useState({
        name:"",
        email:"",
        password:""
    })


    const handleSubmit = (e)=>{
            e.preventDefault()

            dispatch(loginUser(user))
    }
  return (
    <>

    <StyleFrom  onSubmit={handleSubmit}>
        <h2>Login</h2>
        <input type='email' name='' placeholder='Email' onChange={(e)=>setUser({...user,email:e.target.value})}/>
        <input type='password' name='' placeholder='Password' onChange={(e)=>setUser({...user,password:e.target.value})}/>
        <button>  {auth.loginStatus === "pending" ? "Submitting" : "Login"}</button>

        {auth.loginError === "rejected" ?  <p>{auth.registerError}</p> : null}
    </StyleFrom>

    </>
  )
}

export default Login
