                    <p>מיסים ומשלוח מחושבים בקופה</p>

                      {auth._id ? <PayButton cartItems = {cart.cartItems}/> : <button className='cart-login' onClick={()=>{navigate("/login")}}>התחבר</button>}

                  <div className='continue-shopping'>
                <Link to="/">
