import React, { useEffect, useState } from 'react'
import "./Iphone11.css"
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { InputLabel} from '@mui/material';
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';


function Iphone11() {

    const [iphone,setIphone] = useState()
    const [success,setSuccess] = useState(false)


    const [color, setColor] = useState('Red');
    const handleChooseColor = (e) => {
      setColor(e.target.value);
      console.log(e.target.value);   
    }

    const [gb,setGb] = useState('128GB')
    const handleChooseGB = (e) => {
      setGb(e.target.value);
      console.log(e.target.value);
    }



    const [cart, setCart] = useState(false);
    const handleAddCart = () => {
      setCart(true);
      setTimeout(() => {
       setCart("")
      }, 3000);
    };




    //    const getIphone = async ()=>{
    //     const url = "http://localhost:5000/iphone/iphone11"

    //     const response = await fetch(url);
    //     const responseJson = await response.json();

    //     setIphone(responseJson.iphone)
    // }



        // useEffect(()=>{
        //   getIphone()
        // },[])

  return (

  <div className='choose'>
   <Box sx={{ minWidth: 275 }}>
      <Card variant="outlined">
      <CardContent>

    <InputLabel id="demo-simple-select-label">  :בחר צבע</InputLabel>
    <select onChange={handleChooseColor} value={color}>
        <option value="red">Red</option>
        <option value="blue">Blue</option>
        <option value="green">Green</option>
      </select>


    </CardContent>


    <CardContent>
      <InputLabel id="demo-simple-select-label">   :בחר זיכרון</InputLabel>
      <select onChange={handleChooseGB} value={gb}>
        <option value="128GB">128GB</option>
        <option value="256GB">256GB</option>
        <option value="512GB">512GB</option>
        <option value="1TB">1TB</option>
      </select>

    </CardContent>
    </Card>

    {!cart ? <Stack sx={{ width: '100%' }} spacing={2}>



      <Button onClick={handleAddCart} variant="contained">להוסיף לסל</Button>
      </Stack>
    : <Alert  severity="success">Iphone 11 - Color:{color} , GB:{gb} - add to cart!</Alert>}

    </Box>



  </div>
  )
}

export default Iphone11
