import React, { useEffect, useState } from 'react'
import './Cart.css'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom'
import { addToCart, clearCart, decreaseCart, getTotals, removeFromCart } from '../store/CartSlice'
import PayButton from '../auth/payButton/PayButton'



function Cart() {

  const navigate = useNavigate()

  const cart = useSelector((state)=>state.cart)

  const auth = useSelector((state)=>state.auth)

  const [open, setOpen] = useState(true)


  const dispatch = useDispatch()

  useEffect(()=>{
    dispatch(getTotals())
  },[cart,dispatch])

  const handleRemoveFromCart =(cartItem)=>{
      dispatch(removeFromCart(cartItem))
  }


  const handleDecreaseCart = (cartItem)=>{
    dispatch(decreaseCart(cartItem))
  }


  const handleIncreaseQuantity = (cartItem)=>{
    dispatch(addToCart(cartItem))
  }

  const handleClearCart = ()=>{
    dispatch(clearCart())
  }

  return (
<>
<section class="h-screen bg-gray-100 py-12 sm:py-16 lg:py-20">
<div class="mx-auto px-4 sm:px-6 lg:px-8">
    <div class="flex items-center justify-center">
      <h1 class="text-2xl font-semibold text-gray-900">סל קניות</h1>
    </div>



{cart.cartItems.length === 0 ? (
          <div className='cart-empty'>
            <p>Your Cart is currently empty</p>  
            <div className='start-shopping'>
              <Link to="/">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-arrow-left" viewBox="0 0 16 16">
             <path fillRule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"/>
              </svg>
                <span>Start Shopping</span>  
               </Link>
            </div>
           </div>


):(<>

                        {cart.cartItems?.map(cartItem =>

    <div class="mx-auto mt-8 max-w-2xl md:mt-12">
    <div class="bg-white shadow">
      <div class="px-4 py-6 sm:px-8 sm:py-10">
        <div class="flow-root">
          <ul class="-my-8">
            <li class="flex flex-col space-y-3 py-6 text-left sm:flex-row sm:space-x-5 sm:space-y-0">
              <div class="shrink-0">
                <img class="h-24 w-24 max-w-full rounded-lg object-cover" src={cartItem.image.url} alt={cartItem.name} />
              </div>

              <div class="relative flex flex-1 flex-col justify-between">
                <div class="sm:col-gap-5 sm:grid sm:grid-cols-2">
                  <div class="pr-8 sm:pr-5">
                    <p class="text-base font-semibold text-gray-900">{cartItem.name}</p>
                    <p class="mx-0 mt-1 mb-0 text-sm text-gray-400">{cartItem.desc}</p>
                  </div>

                  <div class="mt-4 flex items-end justify-between sm:mt-0 sm:items-start sm:justify-end">
                    <p class="shrink-0 w-20 text-base font-semibold text-gray-900 sm:order-2 sm:ml-8 sm:text-right"> ₪{cartItem.price}</p>

                    <div class="sm:order-1">
                      <div class="mx-auto flex h-8 items-stretch text-gray-600">
                        <button class="flex items-center justify-center rounded-l-md bg-gray-200 px-4 transition hover:bg-black hover:text-white" onClick={()=>handleDecreaseCart(cartItem)}>-</button>
                        <div class="flex w-full items-center justify-center bg-gray-100 px-4 text-xs uppercase transition">{cartItem.cartQuantity}</div>
                        <button class="flex items-center justify-center rounded-r-md bg-gray-200 px-4 transition hover:bg-black hover:text-white"  onClick={()=>handleIncreaseQuantity(cartItem)}>+</button>
                      </div>
                    </div>
                  </div>
                </div>

              </div> 
            </li>
          </ul>
        </div>

      </div>
      </div>
    </div>

                )}

             <div class="mt-6 border-t border-b py-2">
          <div class="flex items-center justify-between">
            <p class="text-sm text-gray-400">Subtotal</p>
            <p class="text-lg font-semibold text-gray-900">₪{cartItem.price * cartItem.cartQuantity}</p>
          </div>
          <div class="flex items-center justify-between">
            <p class="text-sm text-gray-400">Shipping</p>
            <p class="text-lg font-semibold text-gray-900">$8.00</p>
          </div>
        </div>
        <div class="mt-6 flex items-center justify-between">
          <p class="text-sm font-medium text-gray-900">Total</p>
