import React, { useEffect, useState } from 'react'
import './Cart.css'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom'
import { addToCart, clearCart, decreaseCart, getTotals, removeFromCart } from '../store/CartSlice'
import PayButton from '../auth/payButton/PayButton'



function Cart() {

  const navigate = useNavigate()

  const cart = useSelector((state)=>state.cart)

  const auth = useSelector((state)=>state.auth)

  const [open, setOpen] = useState(true)


  const dispatch = useDispatch()

  useEffect(()=>{
    dispatch(getTotals())
  },[cart,dispatch])

  const handleRemoveFromCart =(cartItem)=>{
      dispatch(removeFromCart(cartItem))
  }


  const handleDecreaseCart = (cartItem)=>{
    dispatch(decreaseCart(cartItem))
  }


  const handleIncreaseQuantity = (cartItem)=>{
    dispatch(addToCart(cartItem))
  }

  const handleClearCart = ()=>{
    dispatch(clearCart())
  }

  return (
    <div className='cart-container'>
      <h2>סל קניות</h2>
        {cart.cartItems.length === 0 ? (
          <div className='cart-empty'>
            <p>Your Cart is currently empty</p>  
            <div className='start-shopping'>
              <Link to="/">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-arrow-left" viewBox="0 0 16 16">
  <path fillRule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"/>
</svg>
                <span>Start Shopping</span>  
               </Link>
            </div>
           </div>
        ):(<>


{cart.cartItems?.map(cartItem =>

<div class="h-screen bg-gray-100 pt-20"  key={cartItem._id}>  
<div class="mx-auto max-w-5xl justify-center px-6 md:flex md:space-x-6 xl:px-0">
  <div class="rounded-lg md:w-2/3">
    <div class="justify-between mb-6 rounded-lg bg-white p-6 shadow-md sm:flex sm:justify-start">
      <img src={cartItem.image.url} alt={cartItem.name} class="w-full rounded-lg sm:w-40" />
      <div class="sm:ml-4 sm:flex sm:w-full sm:justify-between">
        <div class="mt-5 sm:mt-0">
          <h2 class="text-lg font-bold text-gray-900">{cartItem.name}</h2>
          <p class="mt-1 text-xs text-gray-700">{cartItem.desc}</p>
        </div>
        <div class="mt-4 flex justify-between sm:space-y-6 sm:mt-0 sm:block sm:space-x-6">
          <div class="flex items-center border-gray-100">
            <span class="cursor-pointer rounded-l bg-gray-100 py-1 px-3.5 duration-100 hover:bg-blue-500 hover:text-blue-50" onClick={()=>handleDecreaseCart(cartItem)}> - </span>
            <input class="h-8 w-8 border bg-white text-center text-xs outline-none" type="number" value={cartItem.cartQuantity} min="1" />
            <span class="cursor-pointer rounded-r bg-gray-100 py-1 px-3 duration-100 hover:bg-blue-500 hover:text-blue-50" onClick={()=>handleIncreaseQuantity(cartItem)}> + </span>
          </div>
          <div class="flex items-center space-x-4">
            <p class="text-sm"> ₪{cartItem.price}</p>
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="h-5 w-5 cursor-pointer duration-150 hover:text-red-500"  onClick={()=>handleRemoveFromCart(cartItem)}>
              <path stroke-linecap="round" stroke-linejoin="round" d="M6 18L18 6M6 6l12 12" />
            </svg>
          </div>
        </div>
      </div>
    </div>

  </div>

          </div>
        </div>        
        )}


      {/* <!-- Sub total --> */}
      <div class="mt-6 h-full rounded-lg border bg-white p-6 shadow-md md:mt-0 md:w-1/3">
        <div class="mb-2 flex justify-between">
          <p class="text-gray-700">Subtotal</p>
          <p class="text-gray-700">$129.99</p>
        </div>
        <div class="flex justify-between">
          <p class="text-gray-700">Shipping</p>
          <p class="text-gray-700">$4.99</p>
        </div>
        <hr class="my-4" />
        <div class="flex justify-between">
          <p class="text-lg font-bold">Total</p>
          <div class="">
            <p class="mb-1 text-lg font-bold">₪{cart.cartTotalAmount}</p>
            <p class="text-sm text-gray-700">including VAT</p>
          </div>
        </div>
        <button class="mt-6 w-full rounded-md bg-blue-500 py-1.5 font-medium text-blue-50 hover:bg-blue-600">Check out</button>
      </div>






        </>)}




  </div>




  )
}

export default Cart


