import React, { useEffect, useState } from 'react'
import "./Iphone11.css"
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import { FormControl, InputLabel, MenuItem, Select} from '@mui/material';
import Alert from '@mui/material/Alert';
import Stack from '@mui/material/Stack';


function Iphone11() {

    const [iphone,setIphone] = useState()
    const [success,setSuccess] = useState(false)


    const [color, setColor] = useState("");
    const handleChooseColor = (e) => {
      setColor(e.target.value);
      console.log(e.target.value);   
    }

    const [gb,setGb] = useState("")
    const handleChooseGB = (e) => {
      setGb(e.target.value);
      console.log(e.target.value);

    }



    const [cart, setCart] = useState(false);
    const handleAddCart = (e) => {
      setCart(true);  
      setTimeout(() => {
      setCart("")
       setColor("")
       setGb("")
      }, 3000);

    };




    //    const getIphone = async ()=>{
    //     const url = "http://localhost:5000/iphone/iphone11"

    //     const response = await fetch(url);
    //     const responseJson = await response.json();

    //     setIphone(responseJson.iphone)
    // }



        // useEffect(()=>{
        //   getIphone()
        // },[])

  return (

  <div className='choose'>

      <h1>מידע</h1>
      <p>
      מעבד A13 Chip
2 מצלמות 12MP
חיישן Face ID
מסך 6.1 אינץ׳ בתצוגת Liquid Retina HD Display</p>




   <Box sx={{ minWidth: 275 }}>
      <Card variant="outlined" className='card'>
      <CardContent>

      <InputLabel id="demo-simple-select-label">:בחר צבע</InputLabel>
    <FormControl fullWidth>
  <Select value={color} onChange={handleChooseColor}>
    <MenuItem  value="Red(PRODUCT)">Red(PRODUCT)</MenuItem>
    <MenuItem  value="Midnight">Midnight</MenuItem>
    <MenuItem  value="Starlight">Starlight</MenuItem>
    <MenuItem  value="Purple">Purple</MenuItem>
  </Select>
</FormControl>
  </CardContent>


    <CardContent>
    <InputLabel id="demo-simple-select-label">:בחר זיכרון</InputLabel>
    <FormControl fullWidth>
  <Select value={gb} onChange={handleChooseGB}>
    <MenuItem value="128GB">128GB</MenuItem>
    <MenuItem value="256GB">256GB</MenuItem>
    <MenuItem value="512GB">512GB</MenuItem>
    <MenuItem value="1TB">1TB</MenuItem>
  </Select>
</FormControl>

    </CardContent>
    </Card>
    </Box>
    {!cart ? <Stack sx={{ width: '100%' }} spacing={2}> 



    <Button className='btnAdd'  onClick={handleAddCart} variant="contained">להוסיף לסל</Button>
    </Stack>
  : <Alert  severity="success" style={{display:"flex",justifyContent:"center"}}>Iphone 11 - Color:{color} , GB:{gb} - add to cart!</Alert>}


  </div>
  )
}

export default Iphone11
