import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import './Navigation.css'
import logoHome from "../images/mepelaozen.png"
import whatsapp from "../images/whatsapp.png"
import facebook from "../images/facebook.png"
import instagram from "../images/instagram.png"
import { useDispatch, useSelector } from 'react-redux'
import styled from 'styled-components'
import { logoutUser } from '../store/AuthSlice'
import { toast } from 'react-toastify'


function Navigation() {

  const dispatch = useDispatch()

  const [nav,setNav] = useState(false)

  const onToggleMenu =(e)=>{

  }





  const {cartTotalQuantity} = useSelector((state)=>state.cart)
  const auth = useSelector((state)=>state.auth)

  return (

    <header className=' bg-white'>
      <hr></hr>
    <nav className='flex justify-between items-center w-[92%] mx-auto'>
          <div>
          <Link to="/home"><img src={logoHome} alt="לוגו למסך הבית" className=' w-44'/></Link>
          </div>


          <div className='nav-links  md:static absolute md:min-h-fit bg-white min-h-[60vh] left-0 top-[-100%] md:w-auto w-full items-center px-5'>  
            <ul className='flex md:flex-row flex-col md:items-center md:gap-[4vw] gap-8 ' >

              <li>
              <Link to="/mac"  className="hover:text-gray-500">Mac</Link>
                </li>



                <li>
                <Link to="/watch"  className="hover:text-gray-500">Watch</Link>

                </li>



                <li>
                <Link to="/ipad"  className="hover:text-gray-500">Ipad</Link>

                </li>




                <li>
                <Link to="/iphone"  className="dropbtn">Iphone</Link>

                </li>




                <li>
                <Link to="/airpods"  className="hover:text-gray-500">AirPods</Link>

                </li>



                <li>
                <Link to="/samsung"  className="hover:text-gray-500">Samsung</Link>
                </li>


                <li>
                <Link to="/case" className="hover:text-gray-500">אביזרים</Link>
                </li>



              <li>
              <Link to="/cart"><svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" fill="currentColor" className="bi bi-handbag-fill" viewBox="0 0 16 16">
                      <path d="M8 1a2 2 0 0 0-2 2v2H5V3a3 3 0 1 1 6 0v2h-1V3a2 2 0 0 0-2-2zM5 5H3.36a1.5 1.5 0 0 0-1.483 1.277L.85 13.13A2.5 2.5 0 0 0 3.322 16h9.355a2.5 2.5 0 0 0 2.473-2.87l-1.028-6.853A1.5 1.5 0 0 0 12.64 5H11v1.5a.5.5 0 0 1-1 0V5H6v1.5a.5.5 0 0 1-1 0V5z"/>
                      </svg> 
                  <span>{cartTotalQuantity}</span>
              </Link>
              </li>


            </ul>

          </div>



          <div>

      {auth._id ? 
      <div>

      <div>
          {auth.isAdmin ?  <Link to="admin/summary">מנהל</Link> : null}

      </div>
      <div className='logout'
       onClick={()=>{
        dispatch(logoutUser(null)); 
        toast.warning("יציאה!",{position:"bottom-center"})
      }}
      >
        יציאה
        </div> 
      </div>
      :
      <div className='flex items-center gap-6'>
     <button> <Link to="/login" className='bg-[#a6c1ee] text-white px-5 p-2 rounded-full hover:bg-[#87acec]'>התחברות</Link></button>
     <button> <Link to="/register" className='bg-[#a6c1ee] text-white px-5 p-2 rounded-full hover:bg-[#87acec]'>הרשמה</Link></button>
        <button onClick={onToggleMenu} className=''>Menu</button>
    </div>
      }
          </div>



    </nav>

    <hr></hr>





  </header>
  )
}

export default Navigation

