import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { registerUser } from '../store/AuthSlice'

function Register() {

    const dispatch = useDispatch()
    const auth = useSelector((state)=>state.auth)

    console.log(auth)

    const [user,setUser] = useState({
        name:"",
        email:"",
        password:""
    })


    const handleSubmit = (e)=>{
            e.preventDefault()

            dispatch(registerUser(user))
    }


  return (
    <>

    <form s onSubmit={handleSubmit}>
        <h2>Register</h2>
        <input type='text' name='' placeholder='Name' onChange={(e)=>setUser({...user,name:e.target.value})}/>
        <input type='email' name='' placeholder='Email' onChange={(e)=>setUser({...user,email:e.target.value})}/>
        <input type='password' name='' placeholder='Password' onChange={(e)=>setUser({...user,password:e.target.value})}/>
        <button>        {auth.registerStatus === "pending" ? "Submitting" : "Register"}</button>

        {auth.registerStatus === "rejected" ?  <p>{auth.registerError}</p> : null}
    </form>

    </>
  )
}

export default Register
