import React, { useEffect, useState } from 'react'
import './Cart.css'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom'
import { addToCart, clearCart, decreaseCart, getTotals, removeFromCart } from '../store/CartSlice'
import PayButton from '../auth/payButton/PayButton'



function Cart() {

  const navigate = useNavigate()

  const cart = useSelector((state)=>state.cart)

  const auth = useSelector((state)=>state.auth)

  const [open, setOpen] = useState(true)


  const dispatch = useDispatch()

  useEffect(()=>{
    dispatch(getTotals())
  },[cart,dispatch])

  const handleRemoveFromCart =(cartItem)=>{
      dispatch(removeFromCart(cartItem))
  }


  const handleDecreaseCart = (cartItem)=>{
    dispatch(decreaseCart(cartItem))
  }


  const handleIncreaseQuantity = (cartItem)=>{
    dispatch(addToCart(cartItem))
  }

  const handleClearCart = ()=>{
    dispatch(clearCart())
  }

  return (
<>
  <div class="bg-gray-100 h-screen py-8">
  <div class="container mx-auto px-4"></div>
  <h1 class="text-2xl font-semibold mb-4">סל קניות</h1>


{cart.cartItems.length === 0 ? (
          <div className='cart-empty'>
            <p>Your Cart is currently empty</p>  
            <div className='start-shopping'>
              <Link to="/">
              <svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-arrow-left" viewBox="0 0 16 16">
             <path fillRule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"/>
              </svg>
                <span>Start Shopping</span>  
               </Link>
            </div>
           </div>


):(<>
            <div class="flex flex-col md:flex-row gap-4">
            <div class="md:w-3/4"></div>
                       <table class="w-full">
                        <thead>
                            <tr>
                                <th class="text-left font-semibold">Product</th>
                                <th class="text-left font-semibold">Price</th>
                                <th class="text-left font-semibold">Quantity</th>
                                <th class="text-left font-semibold">Total</th>
                            </tr>
                            </thead>
                            </table>
                            </div>
              {cart.cartItems?.map(cartItem =>

                     <tbody>
                     <tr>
                         <td class="py-4">
                             <div class="flex items-center">
                                 <img class="h-16 w-16 mr-4" src={cartItem.image.url} alt={cartItem.name}/>
                                 <span class="font-semibold">{cartItem.name}</span>
                             </div>
                         </td>
                         <td class="py-4">₪{cartItem.price}</td>
                         <td class="py-4">
                             <div class="flex items-center">
                                 <button class="border rounded-md py-2 px-4 mr-2"  onClick={()=>handleRemoveFromCart(cartItem)}>-</button>
                                 <span class="text-center w-8">{cartItem.cartQuantity}</span>
                                 <button class="border rounded-md py-2 px-4 ml-2" onClick={()=>handleIncreaseQuantity(cartItem)}>+</button>
                             </div>
                         </td>

                     </tr>

                 </tbody>
              )}

        <div class="md:w-1/4">
                <div class="bg-white rounded-lg shadow-md p-6">
                    <h2 class="text-lg font-semibold mb-4">Summary</h2>
                    <div class="flex justify-between mb-2">
                        <span>Subtotal</span>
                        <span>$19.99</span>
                    </div>
                    <div class="flex justify-between mb-2">
                        <span>Taxes</span>
                        <span>$1.99</span>
                    </div>
                    <div class="flex justify-between mb-2">
                        <span>Shipping</span>
                        <span>$0.00</span>
                    </div>
                    <hr class="my-2"/>
                    <div class="flex justify-between mb-2">
                        <span class="font-semibold">Total</span>
                        <span class="font-semibold">$21.98</span>
                    </div>
                    <button class="bg-blue-500 text-white py-2 px-4 rounded-lg mt-4 w-full">Checkout</button>
                </div>
            </div>








    </>)}
    </div>













    </>
  )
}

export default Cart

